/**
 * User: nross
 * Date: 11/15/11
 * Time: 9:12 AM
 */
var w = 600,
	h = 400;

var maxDataPointsForDots = 50,
	transitionDuration = 1000;

var svg = null,
	yAxisGroup = null,
	xAxisGroup = null,
	dataCirclesGroup = null,
	dataLinesGroup = null;

function draw() {
	var data = generateData();
	var m = 40;
	var max = d3.max(data, function(d) { return d.value });
	var min = 0;
	var pointRadius = 4;
	var xScale = d3.time.scale().range([0, w - m * 2]).domain([data[0].date, data[data.length - 1].date]);
	var yScale = d3.scale.linear().range([h - m * 2, 0]).domain([min, max]);

	var xAxis = d3.svg.axis().scale(xScale).tickSize(h - m * 2).tickPadding(10).ticks(7);
	var yAxis = d3.svg.axis().scale(yScale).orient('left').tickSize(-w + m * 2).tickPadding(10);
	var t = null;

	svg = d3.select('#chart').select('svg').select('g');
	if (svg.empty()) {
		svg = d3.select('#chart')
			.append('svg:svg')
				.attr('width', w)
				.attr('height', h)
				.attr('class', 'viz')
			.append('svg:g')
				.attr('transform', 'translate(' + m + ',' + m + ')');
	}

	t = svg.transition().duration(transitionDuration);

	// y ticks and labels
	if (!yAxisGroup) {
		yAxisGroup = svg.append('svg:g')
			.attr('class', 'yTick')
			.call(yAxis);
	}
	else {
		t.select('.yTick').call(yAxis);
	}

	// x ticks and labels
	if (!xAxisGroup) {
		xAxisGroup = svg.append('svg:g')
			.attr('class', 'xTick')
			.call(xAxis);
	}
	else {
		t.select('.xTick').call(xAxis);
	}

	// Draw the lines
	if (!dataLinesGroup) {
		dataLinesGroup = svg.append('svg:g');
	}

	var dataLines = dataLinesGroup.selectAll('.data-line')
		.data(data);

	dataLines
		.enter()
		.append('svg:line')
			.attr('class', 'data-line')
			.style('opacity', 1e-6)
			.attr('x1', function(d, i) { return (i > 0) ? xScale(data[i - 1].date) : xScale(d.date); })
			.attr('y1', function() { return yScale(0); })
			.attr('x2', function(d) { return xScale(d.date); })
			.attr('y2', function() { return yScale(0); })
		.transition()
		.delay(transitionDuration / 2)
		.duration(transitionDuration)
			.style('opacity', 1)
			.attr('x1', function(d, i) { return (i > 0) ? xScale(data[i - 1].date) : xScale(d.date); })
			.attr('y1', function(d, i) { return (i > 0) ? yScale(data[i - 1].value) : yScale(d.value); })
			.attr('x2', function(d) { return xScale(d.date); })
			.attr('y2', function(d) { return yScale(d.value); });

	dataLines
		.transition()
		.duration(transitionDuration)
			.style('opacity', 1)
			.attr('x1', function(d, i) { return (i > 0) ? xScale(data[i - 1].date) : xScale(d.date); })
			.attr('y1', function(d, i) { return (i > 0) ? yScale(data[i - 1].value) : yScale(d.value); })
			.attr('x2', function(d) { return xScale(d.date); })
			.attr('y2', function(d) { return yScale(d.value); });

	dataLines
		.exit()
		.transition()
		.duration(transitionDuration)
			.style('opacity', 1e-6)
			.attr('y1', function() { return yScale(0); })
			.attr('y2', function() { return yScale(0); })
			.remove();

	// Draw the points
	if (!dataCirclesGroup) {
		dataCirclesGroup = svg.append('svg:g');
	}

	var circles = dataCirclesGroup.selectAll('.data-point')
		.data(data);

	circles
		.enter()
			.append('svg:circle')
				.attr('class', 'data-point')
				.style('opacity', 1e-6)
				.attr('cx', function(d) { return xScale(d.date) })
				.attr('cy', function() { return yScale(0) })
				.attr('r', function() { return (data.length <= maxDataPointsForDots) ? pointRadius : 0 })
			.transition()
			.duration(transitionDuration)
				.style('opacity', 1)
				.attr('cx', function(d) { return xScale(d.date) })
				.attr('cy', function(d) { return yScale(d.value) });

	circles
		.transition()
		.duration(transitionDuration)
			.attr('cx', function(d) { return xScale(d.date) })
			.attr('cy', function(d) { return yScale(d.value) })
			.attr('r', function() { return (data.length <= maxDataPointsForDots) ? pointRadius : 0 })
			.style('opacity', 1);

	circles
		.exit()
			.transition()
			.duration(transitionDuration)
				// Leave the cx transition off. Allowing the points to fall where they lie is best.
				//.attr('cx', function(d, i) { return xScale(i) })
				.attr('cy', function() { return yScale(0) })
				.style("opacity", 1e-6)
				.remove();
}

function generateData() {
	var data = [];
	var i = Math.max(Math.round(Math.random()*100), 3);

	while (i--) {
		var date = new Date();
		date.setDate(date.getDate() - i);
		date.setHours(0, 0, 0, 0);
		data.push({'value' : Math.round(Math.random()*1200), 'date' : date});
	}
	return data;
}

d3.select('#button').on('click', draw);
draw();

